Map::Map(Player* pPlayer) :
	m_handle(-1),
	m_backHandle(-1),
	m_stageNoHandle{},
	m_graphChipNumX(0),
	m_graphChipNumY(0),
	m_chipNo(0),
	m_isGroundHit(false),
	IsWallHit(false),
	IsCeilingHit(false),
	m_chipLeft(0),
	m_chipTop(0),
	m_chipRight(0),
	m_chipBottom(0),
	m_chipTopGround(0),
	m_chipLeftWall(0),
	m_chipRightWall(0),
	m_chipBottomCeiling(0),
	IsStage1(true),
	IsStage2(false),
	IsStage3(false),
	HitSpace(false),
	graphSizeX(0),
	graphSizeY(0)
{
	m_player = pPlayer;
	m_handle = LoadGraph("data/image/map.png");
	m_backHandle = LoadGraph("data/image/blue_bg2.png");

	m_stageNoHandle[0] = LoadGraph("data/image/Stage1.png");
	m_stageNoHandle[1] = LoadGraph("data/image/Stage2.png");
	m_stageNoHandle[2] = LoadGraph("data/image/Stage3.png");
	
	// 読み込んだグラフィックにチップが何個あるかを教えておく
	int graphWidth = 0;
	int graphHeight = 0;
	GetGraphSize(m_handle, &graphWidth, &graphHeight);

	m_graphChipNumX = graphWidth / kChipWidth;
	m_graphChipNumY = graphWidth / kChipHeight;
}

Map::~Map()
{
	DeleteGraph(m_handle);
}

void Map::Update()
{
	m_isGroundHit = false;

	IsWallHit = false;

	MapUpdate();
}

void Map::Draw()
{
	//背景
	DrawGraph(0, 0, m_backHandle, false);

	// マップチップを表示
	for (int y = 0; y < kChipNumY; y++)
	{
		for (int x = 0; x < kChipNumX; x++)
		{
			//データから配置するチップを決定する
			if (IsStage1)
			{
				m_chipNo = kSetChipStage1[y][x];
				DrawGraph(10, 50, m_stageNoHandle[0], true);
			}
			if (IsStage2)
			{
				m_chipNo = kSetChipStage2[y][x];
				GetGraphSize(m_stageNoHandle[1], &graphSizeX, &graphSizeY);
				DrawGraph(Game::kScreenWidth - graphSizeX - 10, 50, m_stageNoHandle[1], true);
			}
			if (IsStage3)
			{
				m_chipNo = kSetChipStage3[y][x];
				DrawGraph(10, 50, m_stageNoHandle[2], true);
			}

			if (m_chipNo < 0)
			{
				//continueは繰り返し分(for,while)の中で使用する命令
				//continueが呼ばれたら以降の繰り返し処理は行わず、次のループに移行する
				continue;
			}

			// 0から始まる通し番号を
			// 上から何個目、左から何個目なのか、という情報に変換する必要がある
			// グラフィックに何個チップが含まれているか、という情報を使用して
			// 計算で求める
			int indexX = m_chipNo % m_graphChipNumX; // 左から何個目のチップか
			int indexY = m_chipNo / m_graphChipNumX; // 上から何個目のチップか

			// チップ番号から切り出し位置を計算する
			int cutX = indexX * kChipWidth;
			int cutY = indexY * kChipHeight;

			DrawRectGraph(x * kChipWidth, y * kChipHeight,
				cutX, cutY, kChipWidth, kChipHeight,
				m_handle, true);
		}
	}
}

void Map::MapUpdate()
{
	for (int y = 0; y < kChipNumY; y++)
	{
		for (int x = 0; x < kChipNumX; x++)
		{
			//データから配置するチップを決定する
			if (IsStage1)
			{
				m_chipNo = kSetChipStage1[y][x];
			}
			if (IsStage2)
			{
				m_chipNo = kSetChipStage2[y][x];
			}
			if (IsStage3)
			{
				m_chipNo = kSetChipStage3[y][x];
			}

			if (m_chipNo < 0)
			{
				//continueは繰り返し分(for,while)の中で使用する命令
				//continueが呼ばれたら以降の繰り返し処理は行わず、次のループに移行する
				continue;
			}

			m_chipLeft = x * kChipWidth;
			m_chipTop = y * kChipHeight;
			m_chipRight = x * kChipWidth + kChipWidth;
			m_chipBottom = y * kChipHeight + kChipHeight;

			//フラグ判定
			bool isPlayerLeft = (m_player->GetRight() <m_chipLeft);//Playerがチップの左側にいる
			bool isPlayerRight = (m_chipRight < m_player->GetLeft());//Playerがチップの右側にいる。
			bool isPlayerTop = (m_player->GetBottom() <m_chipTop);//Playerがチップの上にいる。
			bool isPlayerBottom = (m_chipBottom < m_player->GetTop());//Playerがチップの下にいる。

			//地面に当たった時(7は地面)
			if (m_chipNo == 7)
			{
				if (!(isPlayerLeft || isPlayerRight || isPlayerTop || isPlayerBottom))
				{
					m_isGroundHit = true;

					//当たった床の高さを記録
					m_chipTopGround = m_chipTop;

					/*
					Vec2 NextPos = m_player->GetPos();
					NextPos.y =m_ChiipTop + (m_player->GetBottom() - m_player->GetPos().y);
					m_player->SetPos(NextPos);
					*/
				}
			}

			//天井に当たった時
			if (m_chipNo == 13)
			{
				//天井の判定
				//ロープが当たった時
				if (m_chipLeft < m_player->LinePos.x && m_player->LinePos.x < m_chipRight &&
					m_chipTop < m_player->LinePos.y && m_player->LinePos.y < m_chipBottom)
				{
					//当たった天井の下側のy座標を記録
					m_chipBottomCeiling = m_chipBottom;

					IsCeilingHit = true;
				}
			}

			//壁に当たった時(29は壁)
			if (m_chipNo == 29)
			{
				//右に壁がある場合
				if ((m_player->LastPos.x <m_chipLeft) && !(isPlayerLeft || isPlayerRight || isPlayerTop || isPlayerBottom))
				{
					//当たった壁のX座標を記録
					m_chipLeftWall = m_chipLeft;

					IsWallHit = true;
					Vec2 NextPos = m_player->GetPos();
					NextPos.x =m_chipLeft - (m_player->GetRight() - m_player->GetPos().x) - 0.001f;
					m_player->SetPos(NextPos);
				}
				//左に壁がある場合
				if ((m_chipRight < m_player->LastPos.x) && !(isPlayerLeft || isPlayerRight || isPlayerTop || isPlayerBottom))
				{
					//当たった壁のX座標を記録
					m_chipRightWall = m_chipRight;

					IsWallHit = true;
					Vec2 NextPos = m_player->GetPos();
					NextPos.x =m_chipRight + (m_player->GetRight() - m_player->GetPos().x) + 0.001f;
					m_player->SetPos(NextPos);
				}				
			}			
		}
	}


}